    /**
     * Check if the given array is correctly sorted
     * @param array the array to check
     */
    public void assertArrayOrder(int[] array) {
        for (int i = 0; i < array.length - 1; i++) {
            if (array[i] > array[i + 1]) {
                throw new AssertionFailedError("Array is not correctly sorted!" +
                        " [position="+i+",value="+array[i]+"] is greater than [position="+(i+1)+",value="+array[i+1]+"]");
            }
        }
    }

